import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { getDetail, getDataHome } from '../../redux-modules/modules/Home';
import PageTitle from '../../components/title/Index';
import { MdKeyboardBackspace, MdShare } from 'react-icons/md'

class DetailPage extends React.Component {
    constructor(props) {
        super(props);
        const { match } = props;

        this.state = {
            idParam: match.params.id,
        };
    }

    componentDidMount() {
        const { idParam } = this.state;
        getDetail(idParam);
    }

    render() {
        const { history } = this.props;
        // console.log('match', match);
        // const [idParam] = useState(match.params.id);
        // useEffect(() => {
        //     getDetail(idParam);
        // }, [getDataHome]);
        const { dataDetail } = this.props;
        const { data, loading } = dataDetail;
        console.log(data);
        const backToPrev = (e) => {
            history.goBack();
        };
        return (
            <PageTitle title="Detail">
                <div className="wrapper">
                    <div className="detail">
                        <div className="position-relative container-product">
                            <div className="product-list">
                                <img src="https://0.academia-photos.com/30447813/8838154/9866667/s200_fandi.agus_riyanto.jpg" alt="product" />
                            </div>
                            <MdKeyboardBackspace
                                className="back text-left"
                                onClick={e => { backToPrev(e) }}
                            />
                            <MdShare
                                className="share text-left"
                                onClick={e => { backToPrev(e) }}
                            />
                        </div>
                    </div>
                </div>
            </PageTitle>
        );
    }
}
const mapStateToProps = (state, props) => ({
    dataDetail: state.home,
});

const mapDispatchToProps = dispatch => bindActionCreators({
    getDetail,
}, dispatch);
export default connect(mapStateToProps, mapDispatchToProps)(DetailPage);
