import React, { useEffect, useState, useRef } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { getDataHome } from '../../redux-modules/modules/Home';
import Navigation from '../../components/navigation/Index';
import { IoIosHeartEmpty, IoIosSearch } from "react-icons/io";
import Categories from './Categories';
import Products from './Products';

const Home = (props) => {
    const { getDataHome } = props;
    const inputEl = useRef('');
    useEffect(() => {
        getDataHome();
        inputEl.current.focus();
    }, [getDataHome]);

    const { dataHome } = props;
    console.log('dataHome', dataHome);
    const [dataCategories] = useState(dataHome);
    const { data } = dataHome;

    return (
        <div className="wrapper">
            <Navigation />
            <div className="home">
                <div className="d-flex margin-top-4">
                    <IoIosHeartEmpty className="fa-heart text-left" />
                    <span className="position-relative search text-right">
                        <IoIosSearch className="search-icon" />
                        <input
                            ref={inputEl} 
                            className="input-search"
                            autoComplete="off"
                            type="text"
                            name="search"
                        />
                    </span>
                </div>
                <Categories data={data} />
                <Products data={data} />
            </div>
        </div>
    );
}
const mapStateToProps = (state, props) => ({
    dataHome: state.home,
});

const mapDispatchToProps = dispatch => bindActionCreators({
    getDataHome,
}, dispatch);
export default connect(mapStateToProps, mapDispatchToProps)(Home);
