import React, { useRef, useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import PageTitle from '../../components/title/Index';
import { getDataHome } from '../../redux-modules/modules/Home';
import { IoIosSearch } from 'react-icons/io';
import { MdKeyboardBackspace } from 'react-icons/md'

const SearchPage = (props) => {
    const { getDataHome, history, dataHome } = props;
    const { data } = dataHome;
    let content = [];
    const inputEl = useRef('');
    let [searchResults, setResults] = useState([]);
    let products = [];
    useEffect(() => {
        getDataHome();
    }, [getDataHome]);
    // let [searchName] = useState('');
    // let searchResults = [];

    const backToHome = (e) => {
        e.preventDefault();
        history.push('/home');
    };
    const handleSearch = (e) => {
        e.preventDefault();
        let search = e.target.value;
        if (search) {
            search = search.toLowerCase();
            if (data) {
                if (data.productPromo) {
                    if (data.productPromo.length > 0) {
                        products = data.productPromo;
                        const dataSearch = data.productPromo.filter(x => x.title
                            .toLowerCase()
                            .includes(e.target.value.toLowerCase()));
                        if (dataSearch.length === 0) {
                            searchResults = [];
                            console.log('searchResults', searchResults);
                        } else {
                            searchResults = dataSearch;
                            console.log('searchResults', searchResults);
                        }
                    }
                }
            }
        } else {
            console.log('searchResults', searchResults);
            searchResults = products;
        }
    };
    console.log('searchResults', searchResults);
    // if (searchResults) {
    //     if (searchResults.length > 0) {
    //         console.log('masuk');
    //         searchResults.map((dataResult, indexResult) => {
    //             content.push(
    //                 <div key={indexResult}>{dataResult.title}</div>,
    //             );
    //             return dataResult;
    //         })
    //     }
    // }
    return (
        <PageTitle title="Search">
            <div className="wrapper">
                <div className="home">
                    <div className="d-flex margin-top-4">
                        <MdKeyboardBackspace
                            className="fa-heart text-left"
                            onClick={e => { backToHome(e) }}
                        />
                        <span className="position-relative search text-right">
                            <IoIosSearch className="search-icon" />
                            <input
                                ref={inputEl}
                                className="input-search"
                                autoComplete="off"
                                type="text"
                                name="search"
                                onChange={e => handleSearch(e)}
                            />
                        </span>
                    </div>
                    <div className="margin-top-4">
                        {content}
                    </div>
                </div>
            </div>
        </PageTitle>
    );
}
const mapStateToProps = (state, props) => ({
    dataHome: state.home,
});

const mapDispatchToProps = dispatch => bindActionCreators({
    getDataHome,
}, dispatch);
export default connect(mapStateToProps, mapDispatchToProps)(SearchPage);
