import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import Skeleton from 'react-loading-skeleton';
import { getDataHome, handleLoved } from '../../redux-modules/modules/Home';
import PageTitle from '../../components/title/Index';
import { MdKeyboardBackspace, MdShare } from 'react-icons/md'
import { IoMdHeart, IoIosHeartEmpty } from 'react-icons/io';

const DetailPage = (props) => {
    const {
        getDataHome, history, match,
        handleLoved,
    } = props;
    const idParam = match.params.id;
    useEffect(() => { getDataHome() }, [getDataHome]);
    // const [isLovedStatus, setIsLovedStatus] = useState(false);
    const { dataHome } = props;
    const { data } = dataHome;
    let dataDetail = [];
    let objectDataDetail = null;
        if (data.productPromo) {
            if (data.productPromo.length > 0) {
                dataDetail = data.productPromo.filter(x => x.id === idParam);
            }
        }
    if (dataDetail) {
        if (dataDetail.length > 0) {
            dataDetail.map((detail) => {
                objectDataDetail = detail;
                return detail;
            });
        }
    }
    // const handleLoved = (value) => {
    //     setIsLovedStatus(value);
    // }
    let isLovedContent;
    // let isLovedStatus;
    if (objectDataDetail) {
        console.log('data', objectDataDetail);
        if (objectDataDetail.loved === 1) {
            isLovedContent = (
                <div className="col-sm-2 col-xs-2 col-md-2 text-right" onClick={e => handleLoved(false, objectDataDetail.id, 'loved')}>
                    <IoMdHeart className="loved" />
                </div>
            )
        } else {
            isLovedContent = (
                <div className="col-sm-2 col-xs-2 col-md-2 text-right" onClick={e => handleLoved(true, objectDataDetail.id, 'loved')}>
                    <IoIosHeartEmpty className="loved" />
                </div>
            )
        }
    }
    // if (data) {
    //     if (isLovedStatus) {
    //         isLovedContent =  (
    //             <div className="col-sm-2 col-xs-2 col-md-2 text-right" onClick={e => handleLoved(false)}>
    //                 <IoMdHeart className="loved" />
    //             </div>
    //         )
    //     } else {
    //         isLovedContent =  (
    //             <div className="col-sm-2 col-xs-2 col-md-2 text-right" onClick={e => handleLoved(true)}>
    //                 <IoIosHeartEmpty className="loved" />
    //             </div>
    //         )
    //     }
    // }
    const backToPrev = (e) => {
        e.preventDefault();
        history.goBack();
    };
    return (
        <PageTitle title="Detail">
            <div className="wrapper">
                <div className="detail">
                    {
                        !objectDataDetail
                            ? (
                                <div className="margin-top-4">
                                    <Skeleton height={150} />
                                </div>
                            )
                            : (
                                <div className="position-relative container-product">
                                    <div className="product-list">
                                        <img src={objectDataDetail.imageUrl} alt="product" />
                                    </div>
                                    <MdKeyboardBackspace
                                        className="back text-left"
                                        onClick={e => { backToPrev(e) }}
                                    />
                                    <MdShare
                                        className="share text-left"
                                        onClick={e => { backToPrev(e) }}
                                    />
                                </div>
                            )
                    }
                    {
                        !objectDataDetail
                            ? <Skeleton width="100%" />
                            : (
                                <div className="row">
                                    <div className="col-sm-10 col-xs-10 col-md-10 text-left font-12">
                                        {objectDataDetail.title}
                                    </div>
                                    {isLovedContent}
                                </div>
                            )
                    }
                    {
                        !objectDataDetail
                            ? <Skeleton height="45vh" />
                            : (
                                <div className="margin-top-4 text-justify description">
                                    {objectDataDetail.description}
                                </div>
                            )
                    }
                    {
                        !objectDataDetail
                            ? ''
                            : (
                                <div className="d-flex text-right float-right">
                                    <div className="margin-top-4">
                                        {objectDataDetail.price}
                                    </div>
                                    <button className="margin-left-4 margin-top-2 button-buy" type="submit">Buy</button>
                                </div>
                            )
                    }
                </div>
            </div>
        </PageTitle>
    );
}
const mapStateToProps = (state, props) => ({
    dataHome: state.home,
});

const mapDispatchToProps = dispatch => bindActionCreators({
    getDataHome,
    handleLoved,
}, dispatch);
export default connect(mapStateToProps, mapDispatchToProps)(DetailPage);
