const LOAD = 'modules/Home/LOAD';
const GET_DATA = 'modules/Home/GET_DATA';
const FAIL = 'modules/Home/FAIL';

const initialState = null;

export default function reducer(state = initialState, action = {}) {
    switch (action.type) {
        case LOAD:
            return {
                ...state,
                loaded: false,
                loading: true,
            };

        case GET_DATA:
            let object;
            console.log('action result', action.result);
            action.result.map((data, index) => {
                object = data;
                return data;
            })
            console.log('object', object);

            // if (object.data.category) {
            //     if (object.data.category.length === 0) {
            //         object.data.category.push({
            //             imageUrl: '',
            //             id: 0,
            //             name: '',
            //         });
            //     }
            // }
            return {
                ...object,
                loaded: false,
                loading: true,
            }

        case FAIL:
            delete state.result;
            return {
                loaded: true,
                loading: false,
                result: false,
                error: action.error,
            };
        default:
            return state;
    }
}

export function getDataHome() {
    const url = 'https://private-4639ce-ecommerce56.apiary-mock.com/home';
    return {
        types: [LOAD, GET_DATA, FAIL],
        promise: client => client.get(url, {}),
    };
}
