const LOAD = 'modules/newAssessmentStep1/LOAD';
const LOAD_FAIL = 'modules/newAssessmentStep1/LOAD_FAIL';

const initialState = {
    user: {
        username: 'vandaime',
        fullname: 'Fandi Agus Riyanto',
    },
};

export default function reducer(state = initialState, action = {}) {
    switch (action.type) {
        case LOAD:
            return {
                ...state,
                loaded: false,
                loading: true,
            };

        case LOAD_FAIL:
            delete state.result;

            return {
                loaded: true,
                loading: false,
                result: false,
                error: action.error,
            };
        default:
    }
}

export function getDataLogin(value, index, fieldName) {
    return {
        types: [LOAD, LOAD_FAIL],
        value,
        index,
        fieldName,
    };
}
