const LOAD = 'modules/newAssessmentStep1/LOAD';
const LOAD_FAIL = 'modules/newAssessmentStep1/LOAD_FAIL';

const initialState = null;

export default function reducer(state = initialState, action = {}) {
    switch (action.type) {
        case LOAD:
            return {
                ...state,
                loaded: false,
                loading: true,
            };

        case LOAD_FAIL:
            delete state.result;

            return {
                loaded: true,
                loading: false,
                result: false,
                error: action.error,
            };
        default:
    }
}

export function getDataLogin() {
    return {
        types: [LOAD, LOAD_FAIL],
    };
}
