import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { getDataHome } from '../../redux-modules/modules/Home';
import PageTitle from '../../components/title/Index';
import { MdKeyboardBackspace, MdShare } from 'react-icons/md'

const DetailPage = (props) => {
    const { getDataHome, history, match } = props;
    const idParam = match.params.id;
    useEffect(() => {
        getDataHome();
    }, [getDataHome]);
    const { dataHome } = props;
    const { data, loading } = dataHome;
    if (data) {
        if (data.productPromo) {
            if (data.productPromo.length > 0) {
                data.productPromo.map((product, index) => {
                    console.log('product', product);
                })
            }
        }
    }
    const backToPrev = (e) => {
        history.goBack();
    };
    return (
        <PageTitle title="Detail">
            <div className="wrapper">
                <div className="detail">
                    <div className="position-relative container-product">
                        <div className="product-list">
                            <img src="https://0.academia-photos.com/30447813/8838154/9866667/s200_fandi.agus_riyanto.jpg" alt="product" />
                        </div>
                        <MdKeyboardBackspace
                            className="back text-left"
                            onClick={e => { backToPrev(e) }}
                        />
                        <MdShare
                            className="share text-left"
                            onClick={e => { backToPrev(e) }}
                        />
                    </div>
                </div>
            </div>
        </PageTitle>
    );
}
const mapStateToProps = (state, props) => ({
    dataHome: state.home,
});

const mapDispatchToProps = dispatch => bindActionCreators({
    getDataHome,
}, dispatch);
export default connect(mapStateToProps, mapDispatchToProps)(DetailPage);
